SELECT CourseName, grade from passed_grades, courseoffers, courses WHERE studentid = %1% and passed_grades.degreeid = %2% and courses.courseid = courseoffers.courseid and passed_grades.courseofferid = courseoffers.CourseOfferId ORDER BY Year, Quartile, courseoffers.courseofferid;
SELECT distinct studentId FROM GPA, degrees_where_all_passed, studentregistrationstodegrees WHERE GPA.avgGrade > %1% and GPA.studentregistrationid = degrees_where_all_passed.StudentregistrationId and studentregistrationstodegrees.studentregistrationid = gpa.studentregistrationid ORDER BY studentid;
SELECT degreeid, AVG(case gender when 'F' then 1 else 0 end) as percentage FROM active_students_per_degree, students, studentregistrationstodegrees WHERE studentregistrationstodegrees.studentregistrationid = active_students_per_degree.studentregistrationid and students.studentid = studentregistrationstodegrees.studentid GROUP BY DegreeId ORDER BY degreeid;
SELECT CAST(fem_in_dept.value AS float) / CAST(total_in_dept.value AS float) as percentage FROM fem_in_dept, total_in_dept WHERE fem_in_dept.dept = %1% and total_in_dept.dept = %1%;
with grade_amount as (select courseid, count(grade) as value from with_grade, courseoffers where with_grade.courseofferid = courseoffers.courseofferid group by courseid), passed_amount as (select courseid, count(grade) as value from with_grade, courseoffers where with_grade.courseofferid = courseoffers.courseofferid and grade >= %1% group by courseid) SELECT grade_amount.courseid, (CAST(passed_amount.value as float) / CAST(grade_amount.value as float)) as percentagePassing FROM grade_amount, passed_amount where grade_amount.courseid = passed_amount.courseid;
SELECT distinct studentid, count(studentid) as numberOfCoursesWhereExcellent FROM excellent_students_q6 GROUP BY studentid HAVING count(studentid) >= %1%;
SELECT degreeid, birthyearstudent as birthyear, gender, avg(avgGrade) as avgGrade FROM students, GPA, active_students_per_degree, studentregistrationstodegrees WHERE students.studentid = studentregistrationstodegrees.StudentId and GPA.studentregistrationid = studentregistrationstodegrees.StudentregistrationId  and studentregistrationstodegrees.studentregistrationid = active_students_per_degree.studentregistrationid GROUP BY cube(degreeid, birthyearstudent, gender);
SELECT CourseName, year, quartile from q8, courses, courseoffers where courseoffers.courseid = courses.courseid and q8.courseofferid = courseoffers.courseofferid;